defined('V_LIFE')or die('v');function blk_article($p=''){global $tpl,$cfg,$alt;$o=& $p['prop'];$tpl->reset_theme('block');$tpl->theme('block',(isset($o['tpl'])&&$o['tpl'])?$o['tpl']:'blk_article');$ajx=isset($o['ajx'])&&$o['ajx']?'vf_ajax_'.$p['id']:'';if($ajx){if((isset($cfg['~ajax'])&&$cfg['~ajax'])){echo blk_article_content($p);$tpl->display('block',0,1,1,1);die;}else{$tpl->block('ajax',array('ID'=>$ajx,'URL'=>URL_BASE.VAR_INDEX.'?'.(($cfg['langs']>1&&$alt['lang']!=$cfg['lang'])?VAR_LANG.'='.$alt['lang'].'&':'').VAR_PAGE.'=null&'.VAR_SECTION.'=article&'.VAR_BLOCK.'='.$p['id'],));}}else blk_article_content($p);}function blk_article_content($p=''){global $tpl,$db,$map,$alt,$cfg;$o=& $p['prop'];$id=array();if(isset($o['src'])&&$o['src']){if(is_string($o['src']))$o['src']=array($o['src']);foreach($o['src'] as $v){if(isset($map->i[$v])&&$map->i[$v]['ctype']=='article')$id[]=$v;}}else{for($i=0;$i<$map->c;$i++){if($map->d[$i]['ctype']=='article')$id[]=$map->d[$i]['id'];}}$tpl->reset_block('blk');if($id){$tpl->block('blk',array('CSS'=>$tpl->css($o['css'],'.vf_block'),'TITLE'=>($p['title']{0}=='~')?'':$p['title'],'TAG'=>(isset($o['tag'])&&$o['tag'])?$o['tag']:'h3','URL'=>(isset($map->i[$id[0]]))?URL_BASE.VAR_INDEX.'?'.VAR_PAGE.'='.$map->i[$id[0]]['alias']:'',));$id=implode(',',$id);$sql='SELECT '.(($cfg['sef'])?'alias as id':'id').', cid, title, pic_thumb, preview FROM #__article WHERE cid IN ('.$id.') AND published>0 AND published<'.time().(($cfg['langs']>1)?(' AND ('.(($alt['lang']==$cfg['lang'])?'lang="" OR ':'').'lang="'.$alt['lang'].'")'):'');if($o['typ']=='hot')$sql.=' AND hot=1 ORDER BY rand()';else if($o['typ']=='new')$sql.=' ORDER BY published DESC';else if($o['typ']=='top')$sql.=' ORDER BY hits DESC';else $sql.=' ORDER BY rand()';if(!$db->query($sql,$o['num']))trigger_error($db->error(),E_USER_ERROR);if($db->affected_rows()){$d=$db->fetch();if(isset($o['hot'])&&$o['hot']){for($j=0;$j<$o['hot'];$j++){if($v=array_shift($d)){$v['preview']=(isset($v['preview'])&&$v['preview'])?'<p>'.$v['preview'].'</p>':'';$v['pic_thumb']=(isset($v['pic_thumb'])&&$v['pic_thumb'])?'<img src="'.URL_UPLOAD.$v['pic_thumb'].'" alt="'.$v['title'].'" />':'';$v['U_VIEW']=VAR_INDEX.'?'.(($cfg['langs']>1&&$alt['lang']!=$cfg['lang'])?VAR_LANG.'='.$alt['lang'].'&':'').VAR_PAGE.'='.$map->i[$v['cid']]['alias'].'&'.VAR_ID.'='.$v['id'];$tpl->block('blk.hot',$v,true);}}}$i=1;foreach($d as $k=>$v){$v['preview']=(isset($o['pre'])&&$o['pre'])?'<p>'.$v['preview'].'</p>':'';$v['pic_thumb']=(isset($o['img'])&&$o['img']&&isset($v['pic_thumb'])&&$v['pic_thumb'])?'<img src="'.URL_UPLOAD.$v['pic_thumb'].'" alt="'.$v['title'].'" />':'';$v['U_VIEW']=VAR_INDEX.'?'.(($cfg['langs']>1&&$alt['lang']!=$cfg['lang'])?VAR_LANG.'='.$alt['lang'].'&':'').VAR_PAGE.'='.$map->i[$v['cid']]['alias'].'&'.VAR_ID.'='.$v['id'];$v['N']=$i;$i++;$tpl->block('blk.row',$v,true);}}}}